    //     </div>
    //     <div className="new-expense__control">
    //       <label>Amount</label>
    //       <input
    //         type="number"
    //         min="0.01"
    //         step="0.01"
    //         value={enteredAmount}
    //         onChange={amountChangeHandler}
    //       ></input>
    //     </div>
    //     <div className="new-expense__control">
    //       <label>Date</label>
    //       <input
    //         type="date"
    //         min="2019-01-01"
    //         max="2023-12-31"
    //         value={enteredDate}
    //         onChange={dateChangeHandler}
    //       ></input>
    //     </div>
    //     <div className="new-expense__actuibs">
    //       <button type="submit">Add Expense</button>
    //     </div>
    //   </div>
    // </form>
    {displaybtn}
  );
};
export default ExpenseForm;
