import React, { useState } from "react";
import "./ExpenseForm.css";
const ExpenseForm = (props) => {
  //mutiple state approac
  const [enteredTitle, setEnteredTitle] = useState("");
  const [enteredAmount, setEnteredAmount] = useState("");
  const [enteredDate, setEnteredDate] = useState("");

  const titleChangeHandler = (event) => {
    setEnteredTitle(event.target.value);
  };

  const amountChangeHandler = (event) => {
    setEnteredAmount(event.target.value);
  };

  const dateChangeHandler = (event) => {
    setEnteredDate(event.target.value);
  };

  const submitHandler = (e) => {
    e.preventDefault();
    let expenseData = {
      title: enteredTitle,
      amount: enteredAmount,
      date: new Date(enteredDate),
    };
    props.onSaveExpenseData(expenseData);
    setEnteredTitle("");
    setEnteredAmount("");
    setEnteredDate("");
  };
  //single state approach
  // const [userInput, setUserInput] = useState({
  //   enteredTitle: "",
  //   enteredAmount: "",
  //   enteredDate: "",
  // });
  // const titleChangeHandler = (event) => {
  //   setUserInput((prevState) => {
  //     return { ...prevState, enteredTitle: event.target.value };
  //   });
  // };
  // const amountChangeHandler = (event) => {
  //   setUserInput((prevState) => {
  //     return { ...prevState, enteredAmount: event.target.value };
  //   });
  // };

  // const dateChangeHandler = (event) => {
  //   setUserInput((prevState) => {
  //     return { ...prevState, enteredDate: event.target.value };
  //   });
  // };
  const showForm =()=>{
    setDisplay(<form onSubmit={submitHandler}>
      <div className="new-expense__controls">
        <div className="new-expense__control">
          <label>Title</label>
          <input
            type="text"
            value={enteredTitle}
            onChange={titleChangeHandler}
          ></input>
        </div>
        <div className="new-expense__control">
          <label>Amount</label>
          <input
            type="number"
            min="0.01"
            step="0.01"
            value={enteredAmount}
            onChange={amountChangeHandler}
          ></input>
        </div>
        <div className="new-expense__control">
          <label>Date</label>
          <input
            type="date"
            min="2019-01-01"
            max="2023-12-31"
            value={enteredDate}
            onChange={dateChangeHandler}
          ></input>
        </div>
        <div className="new-expense__actuibs">
          <button type="submit">Add Expense</button>
        </div>
      </div>
    </form>)
  }
  const hideForm = ()=>{
    setDisplay(<button onClick={showForm}>Add Expense</button>)
  }
  const [display,setDisplay] = useState(<div>
    <button onClick={hideForm}>Cancel</button>
    <button onClick={showForm}>Add Expense</button></div>)
  return (
    // <form onSubmit={submitHandler}>
    //   <div className="new-expense__controls">
    //     <div className="new-expense__control">
    //       <label>Title</label>
    //       <input
    //         type="text"
    //         value={enteredTitle}
    //         onChange={titleChangeHandler}
    //       ></input>
    //     </div>
    //     <div className="new-expense__control">
    //       <label>Amount</label>
    //       <input
    //         type="number"
    //         min="0.01"
    //         step="0.01"
    //         value={enteredAmount}
    //         onChange={amountChangeHandler}
    //       ></input>
    //     </div>
    //     <div className="new-expense__control">
    //       <label>Date</label>
    //       <input
    //         type="date"
    //         min="2019-01-01"
    //         max="2023-12-31"
    //         value={enteredDate}
    //         onChange={dateChangeHandler}
    //       ></input>
    //     </div>
    //     <div className="new-expense__actuibs">
    //       <button type="submit">Add Expense</button>
    //     </div>
    //   </div>
    // </form>
    <div>{display}</div>
  );
};
export default ExpenseForm;
